mapper.update:
UPDATE passengers
SET passenger_name = #{passengerName},
    id_card_type = #{idCardType},
    id_card_no = #{idCardNo},
    phone = #{phone},
    passenger_type = #{passengerType},
    is_default = #{isDefault}
WHERE passenger_id = #{passengerId}
  AND user_id = #{userId}

UPDATE passengers
SET is_deleted = 1
WHERE passenger_id = #{passengerId}
  AND user_id = #{userId}

UPDATE passengers
SET is_default = 0
WHERE user_id = #{userId}
